import{_ as l,c as i,o as e,aR as o}from"./chunks/framework.yvlCV3q0.js";const p=JSON.parse('{"title":"Geoserver简介","description":"","frontmatter":{},"headers":[],"relativePath":"docs/basic/intro.md","filePath":"docs/basic/intro.md"}'),r={name:"docs/basic/intro.md"},a=o('<h1 id="geoserver简介" tabindex="-1">Geoserver简介 <a class="header-anchor" href="#geoserver简介" aria-label="Permalink to &quot;Geoserver简介&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">概览</p><p><code>Geoserver</code>是一个基于Java的开源软件平台，用于构建地理信息系统（GIS）和地图服务，实现了OGC的多种开源协议，如WMS、WFS、WMTS等服务，以可视化的方式对数据和服务进行管理，并且提供服务发布后服务的预览。</p></div><h2 id="一、特性" tabindex="-1">一、特性 <a class="header-anchor" href="#一、特性" aria-label="Permalink to &quot;一、特性&quot;">​</a></h2><p>Geoserver具有以下特性：</p><ul><li><strong>兼容性</strong>：Geoserver是OpenGIS Web服务器规范的J2EE实现，兼容WMS、WFS、WMTS、TMS等。</li><li><strong>数据支持</strong>：Geoserver支持多种数据格式，包括PostGIS、Shapefile、MySQL等。</li><li><strong>支持投影</strong>：支持上百种投影。</li><li><strong>输出格式</strong>：能够输出为JPEG、GIF、PNG、SVG、KML、GeoJSON等多种格式。</li><li><strong>易于使用</strong>：Geoserver提供了友好的用户界面和易于使用的API，方便用户进行地图可视化和地理数据处理。</li><li><strong>安全性</strong>：Geoserver提供了安全性和访问控制功能，可以保护地图服务和地理数据的安全。</li><li><strong>社区支持</strong>：Geoserver有一个活跃的社区，为使用者提供技术支持和帮助。</li></ul><h2 id="二、使用技术" tabindex="-1">二、使用技术 <a class="header-anchor" href="#二、使用技术" aria-label="Permalink to &quot;二、使用技术&quot;">​</a></h2><ul><li>Java</li><li>GeoTools</li><li>GeoWebCache</li></ul><h2 id="三、数据支持" tabindex="-1">三、数据支持 <a class="header-anchor" href="#三、数据支持" aria-label="Permalink to &quot;三、数据支持&quot;">​</a></h2><p>Geoserver支持矢量和栅格数据的服务发布，同时也支持对<code>WMS</code>或<code>WMTS</code>服务进行代理，常见的数据格式以及说明如下：</p><ul><li><p><strong>矢量数据源</strong></p><ul><li>Shapefile文件目录</li><li>Shapefile文件</li><li>PostGIS数据库</li><li>Java Properties文件</li><li>WFS服务</li><li>GeoPackage（<em>扩展支持</em>）</li><li>MYSQL数据库（<em>扩展支持</em>）</li><li>MongoDB数据库（<em>扩展支持</em>）</li><li>其他</li></ul></li><li><p><strong>栅格数据源</strong></p><ul><li>ArcGrid</li><li>GeoTIFF</li><li>ImageMosaic（镶嵌影像）</li><li>WorldImage</li><li>GeoPackage (mosaic， <em>扩展支持</em>)</li></ul></li><li><p><strong>其他数据源</strong></p><ul><li>WMS</li><li>WMTS</li></ul></li></ul><h2 id="四、服务支持" tabindex="-1">四、服务支持 <a class="header-anchor" href="#四、服务支持" aria-label="Permalink to &quot;四、服务支持&quot;">​</a></h2><p>Geoserver支持的服务及版本如下：</p><ul><li><strong>WCS</strong><ul><li>1.0.0</li><li>1.1.0</li><li>1.1.1</li><li>1.1</li><li>2.0.1</li></ul></li><li><strong>WFS</strong><ul><li>1.0.0</li><li>1.1.0</li><li>2.0.0</li></ul></li><li><strong>WMS</strong><ul><li>1.1.1</li><li>1.3.0</li></ul></li><li><strong>TMS</strong><ul><li>1.0.0</li></ul></li><li><strong>WMS-C</strong><ul><li>1.1.1</li></ul></li><li><strong>WMTS</strong><ul><li>1.0.0</li></ul></li></ul>',13),t=[a];function s(n,c,u,d,g,h){return e(),i("div",null,t)}const G=l(r,[["render",s]]);export{p as __pageData,G as default};
